CREATE OR REPLACE FUNCTION obter_alteracao_cpoe_conf (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w		timestamp;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	if (obter_se_atendimento_alta(nr_atendimento_p) = 'S') then
		ds_retorno_w := clock_timestamp() + interval '1 days';
	else
		select	max(dt_atualizacao_nrec)
		into STRICT	ds_retorno_w
		from	prescr_medica_cpoe_conf
		where	nr_atendimento = nr_atendimento_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
