						((ie_restricao_prescritor_p = 'F') and (obter_se_medico(obter_pf_usuario(coalesce(a.nm_usuario_nrec,a.nm_usuario),'C'),'M') <> 'S')) or
						((ie_restricao_prescritor_p = 'N') and (coalesce(nr_sequencia::text, '') = '')))) alias123;

	else

		select	max(dt_atualizacao)
		into STRICT	max_atualizacao_w
		from (SELECT	CASE WHEN ie_restricao_prescritor_p='A' THEN coalesce(dt_liberacao_farm,coalesce(dt_liberacao_enf,dt_liberacao)) WHEN ie_restricao_prescritor_p='F' THEN coalesce(dt_liberacao_farm,dt_liberacao_enf) WHEN ie_restricao_prescritor_p='M' THEN dt_liberacao END  dt_atualizacao
				from	cpoe_material a
				where	nr_atendimento = nr_atendimento_p
				
union all

				SELECT	CASE WHEN ie_restricao_prescritor_p='A' THEN coalesce(dt_liberacao_farm,coalesce(dt_liberacao_enf,dt_liberacao)) WHEN ie_restricao_prescritor_p='F' THEN coalesce(dt_liberacao_farm,dt_liberacao_enf) WHEN ie_restricao_prescritor_p='M' THEN dt_liberacao END  dt_atualizacao
				from	cpoe_dieta a
				where	nr_atendimento = nr_atendimento_p
				
union all

				select	CASE WHEN ie_restricao_prescritor_p='A' THEN coalesce(dt_liberacao_farm,coalesce(dt_liberacao_enf,dt_liberacao)) WHEN ie_restricao_prescritor_p='F' THEN coalesce(dt_liberacao_farm,dt_liberacao_enf) WHEN ie_restricao_prescritor_p='M' THEN dt_liberacao END  dt_atualizacao
				from	cpoe_recomendacao a
				where	nr_atendimento = nr_atendimento_p
				
union all

				select	CASE WHEN ie_restricao_prescritor_p='A' THEN coalesce(dt_liberacao_farm,coalesce(dt_liberacao_enf,dt_liberacao)) WHEN ie_restricao_prescritor_p='F' THEN coalesce(dt_liberacao_farm,dt_liberacao_enf) WHEN ie_restricao_prescritor_p='M' THEN dt_liberacao END  dt_atualizacao
				from	cpoe_gasoterapia a
				where	nr_atendimento = nr_atendimento_p
				
union all

				select	CASE WHEN ie_restricao_prescritor_p='A' THEN coalesce(dt_liberacao_farm,coalesce(dt_liberacao_enf,dt_liberacao)) WHEN ie_restricao_prescritor_p='F' THEN coalesce(dt_liberacao_farm,dt_liberacao_enf) WHEN ie_restricao_prescritor_p='M' THEN dt_liberacao END  dt_atualizacao
				from	cpoe_procedimento a
				where	nr_atendimento = nr_atendimento_p
				
union all

				select	CASE WHEN ie_restricao_prescritor_p='A' THEN coalesce(dt_liberacao_farm,coalesce(dt_liberacao_enf,dt_liberacao)) WHEN ie_restricao_prescritor_p='F' THEN coalesce(dt_liberacao_farm,dt_liberacao_enf) WHEN ie_restricao_prescritor_p='M' THEN dt_liberacao END  dt_atualizacao
				from	cpoe_dialise a
				where	nr_atendimento = nr_atendimento_p) alias19;

	end if;

end if;

return	coalesce(max_atualizacao_w,clock_timestamp());

end;
