SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_alteracao_requisito_os (nr_seq_ordem_serv_p bigint) RETURNS bigint AS $body$
DECLARE


/*O objetivo desta function é saber se a OS contém alguma Inclusão, Alteração ou Exclusão de requisito*/

dt_liberacao_w timestamp;
nr_seq_impacto_w bigint;
nr_seq_os_proj_w bigint;
qt_impacto_w 	bigint;

c01 CURSOR FOR
SELECT nr_sequencia, dt_liberacao from man_ordem_serv_impacto where nr_seq_ordem_serv = nr_seq_os_proj_w;


BEGIN

/*Se a OS passada por parâmetro for de projeto, temos que pegar as informações da OS do projeto, que irão ter as Análises de impacto informada */

nr_seq_os_proj_w := coalesce(proj_obter_projeto_ordem_serv(nr_seq_ordem_serv_p),0);

if (nr_seq_os_proj_w > 0)then
	nr_seq_os_proj_w :=  coalesce(proj_obter_dados_projeto(nr_seq_os_proj_w, 'OS'), 0);
end if;

if (nr_seq_os_proj_w = 0)then
	nr_seq_os_proj_w := nr_seq_ordem_serv_p;
end if;

open c01;
loop
fetch c01 into
nr_seq_impacto_w,
dt_liberacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		select	count(*)
		into STRICT	qt_impacto_w
		from	man_ordem_serv_imp_pr
		where	ie_impacto_requisito in ('I', 'A', 'E') /*Esse valores correspodem à Inclusão, Alteração ou Exclusão de requisito*/
		and		nr_seq_impacto = nr_seq_impacto_w;
	end if;

end loop;
close c01;


return	coalesce(qt_impacto_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
