CREATE OR REPLACE FUNCTION obter_altera_orcamento_pac ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_param_5_w			varchar(10);
ie_param_13_w			varchar(10);
nm_usuario_nrec_w		varchar(15);
ie_status_orcamento_w	smallint;
ie_retorno_w			varchar(1)	:= 'N';


BEGIN

ie_param_5_w	:= obter_valor_param_usuario(106, 5, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo);
ie_param_13_w	:= obter_valor_param_usuario(106, 13, obter_perfil_ativo, obter_usuario_ativo	, obter_estabelecimento_ativo);

begin
select	nm_usuario_nrec,
		ie_status_orcamento
into STRICT	nm_usuario_nrec_w,
		ie_status_orcamento_w
from	orcamento_paciente
where	nr_sequencia_orcamento	= nr_sequencia_p;
exception
when others then
		ie_status_orcamento_w	:= 0;
end;

if	((ie_param_5_w	= 'S') or (ie_param_5_w	= 'U') and (nm_usuario_nrec_w = obter_usuario_ativo)) and
	((ie_param_13_w	= 'S') or (ie_status_orcamento_w <> 2)) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
