CREATE OR REPLACE FUNCTION obter_altura_escala (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_altura varchar(12);
			

BEGIN 
begin 
	select 	coalesce(b.qt_altura_cm ,0) 
	into STRICT 	ds_retorno_altura 
	from 	escala_must b 
	where 	b.nr_sequencia = ( 
		SELECT max(nr_sequencia) 
		from 	escala_must 
		where 	ie_situacao  = 'A'							 
		and		nr_atendimento in (SELECT 	x.nr_atendimento 
									from 	atendimento_paciente x 
									where x.cd_pessoa_fisica = obter_dados_atendimento(nr_atendimento_p,'CP')) 
									);
								 
								 
exception 
 when others then 
  ds_retorno_altura:= '0';
end;
 
return	ds_retorno_altura;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
