CREATE OR REPLACE FUNCTION obter_altura_estimada_md ( ie_sexo_p text, qt_idade_P bigint, qt_altura_joelho_p bigint ) RETURNS bigint AS $body$
DECLARE


qt_altura_estimada_w bigint;


BEGIN
    IF ( ie_sexo_p = 'M' ) THEN
        qt_altura_estimada_w := ( 64.19 - ( 0.04 * qt_idade_p ) + ( 2.02 * qt_altura_joelho_p ) );

    ELSE
        qt_altura_estimada_w := ( 84.88 - ( 0.24 * qt_idade_p ) + ( 1.83 * qt_altura_joelho_p ) );
    END IF;

    RETURN qt_altura_estimada_w;

END;
