SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_amostras_seq_prescr (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w		smallint;
ie_exame_amostra_w			varchar(1);
nr_seq_prescr_proc_mat_w	bigint;
ds_amostra_w				varchar(300) := '';


c01 CURSOR FOR
SELECT	nr_seq_prescr_proc_mat
from	prescr_proc_mat_item
where	nr_prescricao = nr_prescricao_p
and		nr_seq_prescr = nr_seq_prescr_p;


BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	ie_exame_amostra
into STRICT	ie_exame_amostra_w
from	lab_parametro
where	cd_estabelecimento = cd_estabelecimento_w;

if (ie_exame_amostra_w = 'S') then

	open c01;
	loop
	fetch c01 into
		nr_seq_prescr_proc_mat_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		/* Restrição de tamanho incluído para evitar exceção quando o usuário cadastrar qt_coleta (exame_lab_material) com valor irreal*/

		if length(coalesce(ds_amostra_w, 0)) < 290 then
			ds_amostra_w := ds_amostra_w||nr_seq_prescr_proc_mat_w||',';
		end if;
		end;
	end loop;
	close c01;

end if;


return	ds_amostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
