CREATE OR REPLACE FUNCTION obter_amostra_precaucao (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80);
ds_amostra_w	varchar(80);
cd_amostra_cultura_w	numeric(20);

C01 CURSOR FOR
	SELECT	cd_amostra_cultura
	from	atend_precaucao_amostra
	where	nr_seq_atend_precaucao = nr_sequencia_p;


BEGIN


open C01;
loop
fetch C01 into
	cd_amostra_cultura_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select 	max(ds_amostra_cultura)
	into STRICT	ds_amostra_w
	from 	cih_amostra_cultura
	where	cd_amostra_cultura = cd_amostra_cultura_w;

	ds_retorno_w := 	ds_retorno_w||' / '||ds_amostra_w;

	end;
end loop;
close C01;
ds_retorno_w :=	substr(ds_retorno_w,4,length(ds_retorno_w));
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
