CREATE OR REPLACE FUNCTION obter_analise_precaucao_js ( nr_atendimento_p bigint, nr_seq_controle_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	begin

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	atendimento_precaucao a
	where	coalesce(a.dt_analise::text, '') = ''
	and	not exists (	SELECT	1
				from	cih_controle_atend b
				where	b.nr_atendimento = a.nr_atendimento
				and	coalesce(b.dt_finalizacao::text, '') = ''
				and 	((b.nr_seq_controle = nr_seq_controle_p) or (coalesce(nr_seq_controle_p::text, '') = '') or (nr_seq_controle_p = 0)))
	and	coalesce(a.dt_inativacao::text, '') = ''
	and	a.nr_atendimento = nr_atendimento_p;
	end;
end if;

return	ds_retorno_w;

end;
