CREATE OR REPLACE FUNCTION obter_anestesista_cirurgia (nr_cirurgia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(90);
cd_medico_anestesista_w		varchar(10);
nm_medico_anestesista_w		varchar(90);
		

BEGIN 
ds_retorno_w	:= null;
 
if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then 
 
	select 	max(a.cd_medico_anestesista), 
		max(substr(obter_nome_pf(p.cd_pessoa_fisica),1,90)) 
	into STRICT	cd_medico_anestesista_w, 
		nm_medico_anestesista_w 
	from	pessoa_fisica p, 
		cirurgia a 
	where	a.cd_medico_anestesista = p.cd_pessoa_fisica 
	and	nr_cirurgia = nr_cirurgia_p;
	 
	if (ie_opcao_p = 'C') then 
		ds_retorno_w	:= cd_medico_anestesista_w;
	else 
		ds_retorno_w	:= nm_medico_anestesista_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
