CREATE OR REPLACE FUNCTION obter_anest_regra_agenda ( cd_medico_p text) RETURNS varchar AS $body$
DECLARE



c01 CURSOR FOR
	SELECT	cd_anestesista
	from	regra_define_anest_agenda
	where (cd_medico = cd_medico_p) or (obter_se_medico_equipe(nr_seq_equipe,cd_medico_p) = 'S')
	order by CASE WHEN coalesce(cd_medico::text, '') = '' THEN 998  ELSE 1 END  desc,
		 CASE WHEN coalesce(nr_seq_equipe::text, '') = '' THEN 999  ELSE 2 END;

cd_anestesista_w	varchar(14) := null;
qt_registros_w		bigint;


BEGIN

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	select	count(*)
	into STRICT	qt_registros_w
	from	regra_define_anest_agenda
	where	coalesce(ie_situacao,'A') = 'A';
	if (qt_registros_w > 0) then
		open C01;
		loop
		fetch C01 into
			cd_anestesista_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			cd_anestesista_w := cd_anestesista_w;
			end;
		end loop;
		close C01;
	end if;
end if;

return cd_anestesista_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
