CREATE OR REPLACE FUNCTION obter_apelido_escala (nr_seq_escala_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_classif_w	bigint;
nr_seq_grupo_w		bigint;
ds_apelido_w		varchar(80);
ds_retorno_w		varchar(80);


BEGIN

select 	max(nr_seq_grupo)
into STRICT	nr_seq_grupo_w
from	escala
where 	nr_sequencia = nr_seq_escala_p;

select  max(nr_seq_classif)
into STRICT	nr_seq_classif_w
from	escala_grupo
where	nr_sequencia = nr_seq_grupo_w;

select	max(ds_apelido)
into STRICT	ds_apelido_w
from	escala_profissional
where	cd_profissional = cd_pessoa_fisica_p
and	nr_seq_classif = nr_seq_classif_w;

if (ie_opcao_p = 'A') then
	ds_retorno_w	:= ds_apelido_w;
end if;
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
