CREATE OR REPLACE FUNCTION obter_aplicacao_item_prescr (nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE



qt_hr_aplicacao_w	smallint;
qt_mi_aplicacao_w	smallint;
qt_solucao_w		double precision;
ds_aplicacao_w	varchar(255);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	max(qt_hora_aplicacao),
		max(qt_min_aplicacao),
		max(qt_solucao)
	into STRICT	qt_hr_aplicacao_w,
		qt_mi_aplicacao_w,
		qt_solucao_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_item_p;

	if (qt_hr_aplicacao_w IS NOT NULL AND qt_hr_aplicacao_w::text <> '') then
		ds_aplicacao_w := ajustar_valor_numerico(qt_hr_aplicacao_w) || 'h';
	end if;

	if (qt_mi_aplicacao_w IS NOT NULL AND qt_mi_aplicacao_w::text <> '') then
		if (ds_aplicacao_w IS NOT NULL AND ds_aplicacao_w::text <> '') then
			ds_aplicacao_w := ds_aplicacao_w || ' ' || to_char(qt_mi_aplicacao_w) || 'min';
		else
			ds_aplicacao_w := ajustar_valor_numerico(qt_mi_aplicacao_w) || 'min';
		end if;
	end if;

	if (qt_solucao_w IS NOT NULL AND qt_solucao_w::text <> '') then
		if (ds_aplicacao_w IS NOT NULL AND ds_aplicacao_w::text <> '') then
			ds_aplicacao_w := ds_aplicacao_w || ' ' || ajustar_valor_numerico(qt_solucao_w) || OBTER_UNID_MED_USUA('ml');
		else
			ds_aplicacao_w := ajustar_valor_numerico(qt_solucao_w) || OBTER_UNID_MED_USUA('ml');
		end if;
	end if;

	if (ds_aplicacao_w IS NOT NULL AND ds_aplicacao_w::text <> '') then
		ds_aplicacao_w := '(' || ds_aplicacao_w || ')';
	end if;

end if;

return ds_aplicacao_w;

end;
$body$
