SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_aplicacao_prev_versao ( nr_seq_prev_versao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p:
	C - Código/sequência da aplicação
	D - Descrição da aplicação	*/
nr_seq_aplicacao_w		bigint;
ds_aplicacao_w			varchar(255);
ds_retorno_w			varchar(255);


BEGIN

if (coalesce(nr_seq_prev_versao_p,0) <> 0) then
	begin
	select	a.nr_sequencia,
		a.ds_aplicacao
	into STRICT	nr_seq_aplicacao_w,
		ds_aplicacao_w
	from	atualizacao_tasy b,
		aplicacao_software a
	where	a.nr_sequencia = b.nr_seq_aplicacao
	and	b.nr_sequencia = nr_seq_prev_versao_p;

	if (ie_opcao_p = 'C') then
		ds_retorno_w := nr_seq_aplicacao_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w := ds_aplicacao_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
