CREATE OR REPLACE FUNCTION obter_apolice_conv (nr_atendimento_p bigint, cd_convenio_parametro_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	convenio_apolice.nr_apolice%type;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	
	select	c.nr_apolice
	into STRICT	ds_retorno_w
	from	convenio_documento a,
			apolice_classe b,
			convenio_apolice c,
			pessoa_titular_convenio d,
			atendimento_paciente e
	where	a.cd_convenio = b.cd_convenio
	and		a.nr_seq_cobertura = b.nr_seq_cobertura
	and		c.nr_sequencia = b.nr_seq_apolice
	and		c.nr_apolice = d.nr_apolice
	and		c.cd_convenio = d.cd_convenio
	and		e.cd_pessoa_fisica = d.cd_pessoa_fisica
	and		c.cd_convenio = cd_convenio_parametro_p
	and		e.nr_atendimento = nr_atendimento_p
	and		d.cd_plano_convenio = b.cd_plano;
	
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
