SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_approv_status (NR_SEQ_DOC_P bigint, CD_FUNCAO_P bigint) RETURNS bigint AS $body$
DECLARE


ret_value bigint;


BEGIN
select
    count(*) into STRICT ret_value
from 
    mult_aprov_item a 
where 
    ((a.nr_seq_funcao = (SELECT nr_sequencia from mult_aprov_funcao b where b.cd_funcao  = CD_FUNCAO_P)
and
    a.nr_seq_doc = NR_SEQ_DOC_P)
or (NR_SEQ_DOC_P = 120757 and CD_FUNCAO_P = 872 and a.nr_seq_funcao = 874))
and (select count(*) from mult_aprov_prof c where c.nr_seq_item = a.nr_sequencia) > 0;

return ret_value;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
