CREATE OR REPLACE FUNCTION obter_apresent_item_nf (nr_sequencia_p bigint, nr_item_nf_p bigint) RETURNS varchar AS $body$
DECLARE


cd_unidade_medida_compra_w	varchar(30);
qt_item_nf_w			double precision;
qt_item_estoque_w		double precision;
qt_conv_w			double precision;
ds_resultado_w			varchar(80);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_item_nf_p IS NOT NULL AND nr_item_nf_p::text <> '') then
	select	cd_unidade_medida_compra,
		qt_item_nf,
		qt_item_estoque
	into STRICT	cd_unidade_medida_compra_w,
		qt_item_nf_w,
		qt_item_estoque_w
	from	nota_fiscal_item
	where	nr_Sequencia		= nr_sequencia_p
	and	nr_item_nf	 	= nr_item_nf_p;
end if;

qt_conv_w	:= dividir(qt_item_estoque_w, qt_item_nf_w);

if (qt_conv_w	<= 1) then
	ds_resultado_w	:=  cd_unidade_medida_compra_w;
else
	ds_resultado_w	:=  cd_unidade_medida_compra_w || ' ' || qt_conv_w;
end if;

RETURN	ds_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
