CREATE OR REPLACE FUNCTION obter_apres_cat_fluxo ( nr_seq_categoria_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_retorno_w bigint;
nr_categoria_w	bigint;


BEGIN

	nr_retorno_w := 0;
	if (nr_seq_categoria_p > 0) then
		select count(*)
		into STRICT nr_categoria_w
		from manchester_cat_fluxo_cli
		where nr_seq_categoria_cad = nr_seq_categoria_p;
		if ( nr_categoria_w > 0) then
			select max(nr_seq_apres)
			into STRICT nr_retorno_w
			from manchester_cat_fluxo_cli
			where nr_seq_categoria_cad = nr_seq_categoria_p;
		else
			select max(nr_seq_apresentacao)
			into STRICT nr_retorno_w
			from manchester_categoria_fluxo
			where nr_sequencia = nr_seq_categoria_p;
		end if;
	end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
