CREATE OR REPLACE FUNCTION obter_aprovacao_fornec_docto (nr_seq_fornec_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_docto_w	varchar(1);


BEGIN

begin
	select	max('P')
	into STRICT	ie_status_docto_w
	from	reg_lic_fornec_docto
	where 	nr_seq_fornec = nr_seq_fornec_p
	and	coalesce(dt_aprovacao::text, '') = '';
	exception
	when no_data_found then
		ie_status_docto_w := 'T';
end;

return	coalesce(ie_status_docto_w,'T');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
