SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_area_codificao_geta (nr_seq_status_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_areia_w		bigint;
ds_status_w		varchar(255);
ds_cor_w		varchar(10);
nm_area_w		varchar(255);
ds_retorno_w		varchar(255);

-- ie_opcao = 'S' (STATUS); ie_opcao = 'C' (COR); ie_opcao = 'A' (AREA);
BEGIN

if (coalesce(nr_seq_status_p,0) > 0) then

	select  max(NR_SEQ_AREA),
		max(DS_STATUS),
		max(coalesce(ds_cor,'clwhite'))
	into STRICT	nr_seq_areia_w,
		ds_status_w,
		ds_cor_w
	from    STATUS_CODIFICACAO
	where   nr_sequencia = nr_seq_status_p;


	if (nr_seq_areia_w > 0) then
		select  max(substr(nm_area,1,255))
		into STRICT	nm_area_w
		from 	AREA_STATUS_CODIFICACAO
		where 	nr_sequencia = nr_seq_areia_w;
	end if;

end if;

if (ie_opcao_p = 'S') then
	ds_retorno_w := ds_status_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := ds_cor_w;
elsif (ie_opcao_p = 'A') then
	ds_retorno_w := nm_area_w;

end if;

return	substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_area_codificao_geta (nr_seq_status_p bigint, ie_opcao_p text) FROM PUBLIC;

