CREATE OR REPLACE FUNCTION obter_area_gerencia_org_usu ( nm_usuario_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE



ie_area_gerencia_w	varchar(15);


BEGIN

select	coalesce(max(g.ie_area_gerencia),'XXX')
into STRICT	ie_area_gerencia_w
from	gerencia_wheb g,
	GERENCIA_WHEB_GRUPO y,
	GERENCIA_WHEB_GRUPO_USU x
where	y.nr_sequencia = x.nr_seq_grupo
and	y.nr_seq_gerencia	= g.nr_sequencia
and	upper(x.nm_usuario_grupo) = upper(nm_usuario_p)
and	dt_referencia_p between coalesce(x.dt_inicio,dt_referencia_p) and coalesce(x.dt_fim,dt_referencia_p)
and	coalesce(g.ie_situacao,'A')	= 'A'
and	coalesce(y.ie_situacao,'A')	= 'A';

return ie_area_gerencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
