CREATE OR REPLACE FUNCTION obter_area_irradiada ( nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1000);
cd_doenca_cid_w	varchar(10);
ds_doenca_cid_w	varchar(80);

C01 CURSOR FOR
	SELECT	cd_doenca_cid,
		substr(Obter_Desc_cid_doenca(cd_doenca_cid),1,80)
	from	sus_laudo_area_irradiada
	where	nr_seq_laudo	= nr_seq_interno_p;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
		cd_doenca_cid_w,
		ds_doenca_cid_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w	:= ds_retorno_w || cd_doenca_cid_w ||'    '||ds_doenca_cid_w ||chr(13) || chr(10);
END LOOP;
Close C01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
