SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_armazenamento ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w					varchar(2000);
cd_material_reconstituicao_w	integer;
cd_material_rediluicao_w		integer;
cd_material_diluicao_w			integer;
cd_material_w					integer;
ds_seq_estagio_w				varchar(255);
qt_registro_w					bigint;
ds_forma_armaz_w				varchar(255);
qt_estabilidade_w				double precision;
ds_tempo_w						varchar(255);
cd_estabelecimento_w			estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	substr(obter_descricao_padrao('MAT_FORMA_ARMAZ','DS_FORMA_ARMAZ',nr_seq_forma),1,80),
			qt_estabilidade,
			substr(obter_valor_dominio(1246, ie_tempo_estab),1,254)
	from	material_armazenamento
	where	cd_material	=	cd_material_w
	and		obter_se_contido(nr_seq_estagio,ds_seq_estagio_w) = 'S'
	and 	coalesce(cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w;


BEGIN

cd_estabelecimento_w := coalesce(wheb_usuario_pck.get_cd_estabelecimento, 0);

/* Material */

select	max(coalesce(cd_material,0))
into STRICT	cd_material_w
from 	prescr_material
where 	nr_sequencia = nr_sequencia_p
and 	nr_prescricao = nr_prescricao_p;

/* Obter se possuiu regras */

select	count(*)
into STRICT	qt_registro_w
from	material_armazenamento
where	cd_material	=	cd_material_w
and 	coalesce(cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w;

/* Diluição */

select	max(coalesce(cd_material,0))
into STRICT	cd_material_diluicao_w
from 	prescr_material
where 	((nr_sequencia_diluicao = nr_sequencia_p) or (nr_sequencia = nr_sequencia_p))
and 	nr_prescricao = nr_prescricao_p
and		ie_agrupador IN (3);

/* Rediluição */

select	max(coalesce(cd_material,0))
into STRICT	cd_material_rediluicao_w
from 	prescr_material
where 	((nr_sequencia_diluicao = nr_sequencia_p) or (nr_sequencia = nr_sequencia_p))
and 	nr_prescricao = nr_prescricao_p
and		ie_agrupador IN (7);

/* Reconstituição */

select	max(coalesce(cd_material,0))
into STRICT	cd_material_reconstituicao_w
from 	prescr_material
where 	((nr_sequencia_diluicao = nr_sequencia_p) or (nr_sequencia = nr_sequencia_p))
and 	nr_prescricao = nr_prescricao_p
and		ie_agrupador IN (9);

if (qt_registro_w > 0) and (cd_material_reconstituicao_w IS NOT NULL AND cd_material_reconstituicao_w::text <> '') and (coalesce(cd_material_rediluicao_w::text, '') = '') and (coalesce(cd_material_diluicao_w::text, '') = '') then
	ds_seq_estagio_w	:= '2';
elsif (qt_registro_w > 0) and
	((cd_material_reconstituicao_w IS NOT NULL AND cd_material_reconstituicao_w::text <> '') and (cd_material_diluicao_w IS NOT NULL AND cd_material_diluicao_w::text <> '') and (coalesce(cd_material_rediluicao_w::text, '') = '')) or
	((cd_material_diluicao_w IS NOT NULL AND cd_material_diluicao_w::text <> '') and (coalesce(cd_material_rediluicao_w::text, '') = '') and (coalesce(cd_material_reconstituicao_w::text, '') = '')) then
	ds_seq_estagio_w	:= '1';
elsif (qt_registro_w > 0) and (coalesce(cd_material_reconstituicao_w::text, '') = '') and (coalesce(cd_material_diluicao_w::text, '') = '') then
	ds_seq_estagio_w	:= '3,4';
else
	ds_seq_estagio_w	:= '-1';
end if;

if (ds_seq_estagio_w <> '-1') then
	open C01;
	loop
	fetch C01 into
		ds_forma_armaz_w,
		qt_estabilidade_w,
		ds_tempo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w	:= ds_retorno_w || obter_desc_expressao(347075,'DS_FORMA_ARMAZ_W='|| ds_forma_armaz_w||';QT_ESTABILIDADE_W='||to_char(qt_estabilidade_w) || ';DS_TEMPO_W='|| ds_forma_armaz_w);
		end;
	end loop;
	close C01;
else
	ds_retorno_w	:= obter_desc_expressao(347074);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_armazenamento ( nr_prescricao_p bigint, nr_sequencia_p bigint) FROM PUBLIC;

