CREATE OR REPLACE FUNCTION obter_artigo_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE



ie_artigo_w			varchar(01);
ie_sexo_w			varchar(01);


BEGIN

select	ie_sexo
into STRICT	ie_sexo_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (ie_sexo_w	= 'M') then
	ie_artigo_w		:= 'o';
elsif (ie_sexo_w	= 'F') then
	ie_artigo_w			:= 'a';
end if;

return ie_artigo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
