CREATE OR REPLACE FUNCTION obter_asa_apae ( nr_seq_aval_pre_p bigint) RETURNS varchar AS $body$
DECLARE


ie_asa_w	varchar(15);


BEGIN

select	CASE WHEN ie_emergencia='S' THEN CASE WHEN ie_asa=1 THEN 'I' WHEN ie_asa=2 THEN 'II' WHEN ie_asa=3 THEN 'III' WHEN ie_asa=4 THEN 'IV' WHEN ie_asa=5 THEN 'V' WHEN ie_asa=6 THEN 'VI' END  ||					'E'  ELSE CASE WHEN ie_asa=1 THEN 'I' WHEN ie_asa=2 THEN 'II' WHEN ie_asa=3 THEN 'III' WHEN ie_asa=4 THEN 'IV' WHEN ie_asa=5 THEN 'V' WHEN ie_asa=6 THEN 'VI' END  END  ie_asa
into STRICT	ie_asa_w
from	asa_apae
where	nr_seq_aval_pre	=	nr_seq_aval_pre_p;

return	ie_asa_w;

end;
$body$
