SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_aspecto_mat_inspecao ( nr_sequencia_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mostra_w			varchar(1) := 'N';
qt_existe_w			bigint;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material	= cd_material_p;

select	count(*)
into STRICT	qt_existe_w
from	inspecao_rec_aspecto
where	nr_sequencia		= nr_sequencia_p
and	cd_grupo_material	= cd_grupo_material_w;
if (qt_existe_w > 0) and (ie_mostra_w = 'N') then
	ie_mostra_w := 'S';
end if;

select	count(*)
into STRICT	qt_existe_w
from	inspecao_rec_aspecto
where	nr_sequencia		= nr_sequencia_p
and	cd_subgrupo_material	= cd_subgrupo_material_w;
if (qt_existe_w > 0) and (ie_mostra_w = 'N') then
	ie_mostra_w := 'S';
end if;

select	count(*)
into STRICT	qt_existe_w
from	inspecao_rec_aspecto
where	nr_sequencia		= nr_sequencia_p
and	cd_classe_material	= cd_classe_material_w;
if (qt_existe_w > 0) and (ie_mostra_w = 'N') then
	ie_mostra_w := 'S';
end if;

select	count(*)
into STRICT	qt_existe_w
from	inspecao_rec_aspecto
where	nr_sequencia		= nr_sequencia_p
and	cd_material		= cd_material_p;
if (qt_existe_w > 0) and (ie_mostra_w = 'N') then
	ie_mostra_w := 'S';
end if;

select	count(*)
into STRICT	qt_existe_w
from	inspecao_rec_aspecto
where	nr_sequencia		= nr_sequencia_p
and	coalesce(cd_material::text, '') = ''
and	coalesce(cd_classe_material::text, '') = ''
and	coalesce(cd_subgrupo_material::text, '') = ''
and	coalesce(cd_grupo_material::text, '') = '';
if (qt_existe_w > 0) and (ie_mostra_w = 'N') then
	ie_mostra_w := 'S';
end if;

return	ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
