CREATE OR REPLACE FUNCTION obter_assinatura_enfermagem (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255):=null;
nr_seq_assinatura_w		varchar(15):=null;
nr_seq_assinatura_farm_w	varchar(15):=null;
nr_seq_assinatura_enf_w		varchar(15):=null;
nr_seq_assin_prim_hor_w		varchar(15):=null;
nr_seq_assin_apraza_w		varchar(15):=null;


BEGIN

select	max(a.nr_seq_assinatura),
	max(a.nr_seq_assinatura_farm),
	max(a.nr_seq_assinatura_enf),
	max(b.nr_seq_assin_prim_hor),
	max(b.nr_seq_assin_apraza)
into STRICT	nr_seq_assinatura_w,
	nr_seq_assinatura_farm_w,
	nr_seq_assinatura_enf_w,
	nr_seq_assin_prim_hor_w,
	nr_seq_assin_apraza_w
from	prescr_medica a,
	prescr_material b
where	a.nr_prescricao = b.nr_prescricao
and	a.nr_prescricao = nr_prescricao_p;

if (nr_seq_assinatura_w IS NOT NULL AND nr_seq_assinatura_w::text <> '') then
	ds_retorno_w := nr_seq_assinatura_w;
end if;

if (nr_seq_assinatura_enf_w IS NOT NULL AND nr_seq_assinatura_enf_w::text <> '') then
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := nr_seq_assinatura_enf_w;
	else
		ds_retorno_w := ds_retorno_w||','||nr_seq_assinatura_enf_w;
	end if;
end if;

if (nr_seq_assinatura_farm_w IS NOT NULL AND nr_seq_assinatura_farm_w::text <> '') then
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := nr_seq_assinatura_farm_w;
	else
		ds_retorno_w := ds_retorno_w||','||nr_seq_assinatura_farm_w;
	end if;
end if;


if (nr_seq_assin_prim_hor_w IS NOT NULL AND nr_seq_assin_prim_hor_w::text <> '') then
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := nr_seq_assin_prim_hor_w;
	else
		ds_retorno_w := ds_retorno_w||','||nr_seq_assin_prim_hor_w;
	end if;
end if;

if (nr_seq_assin_apraza_w IS NOT NULL AND nr_seq_assin_apraza_w::text <> '') then
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := nr_seq_assin_apraza_w;
	else
		ds_retorno_w := ds_retorno_w||','||nr_seq_assin_apraza_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
