CREATE OR REPLACE FUNCTION obter_atecaco_atend_tipo_conv (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_atecaco_w			bigint;


BEGIN

select	coalesce(max(nr_seq_interno),0)
into STRICT	nr_seq_atecaco_w
from 	convenio b,
	atend_categoria_convenio a
where	a.nr_atendimento 		= nr_atendimento_p
  and 	a.cd_convenio		= b.cd_convenio
  and	b.ie_tipo_convenio		<> 3;

RETURN nr_seq_atecaco_w;

END;
