CREATE OR REPLACE FUNCTION obter_atendimentos_diferentes (nr_atendimento_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(4000);
ds_retorno_w1	varchar(4000);

C01 CURSOR FOR	 
	SELECT nr_atendimento 
	from  cih_ficha_ocorrencia 
	where obter_pessoa_atendimento(nr_atendimento, 'C') = cd_pessoa_fisica_p 
	and  nr_atendimento <> nr_atendimento_p;


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
open C01;
loop 
fetch C01 into	 
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_retorno_w1 := ds_retorno_w1 ||', '|| ds_retorno_w;
	end;
end loop;
close C01;
end if;
return substr(ds_retorno_w1,2,250);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
