CREATE OR REPLACE FUNCTION obter_atendimentos_medico (cd_medico_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_inicio_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE

			 
c01 CURSOR FOR 
SELECT 	nr_atendimento 
from 	atendimento_paciente 
where	nr_atendimento in ( 
	SELECT	a.nr_atendimento 
	from	cirurgia a, 
		atendimento_paciente b, 
		pessoa_fisica c 
	where	a.nr_atendimento    = b.nr_atendimento 
	and	b.cd_pessoa_fisica   = c.cd_pessoa_fisica 
	and	a.cd_procedimento_princ = cd_procedimento_p 
	and	a.ie_origem_proced   = ie_origem_proced_p 
	and	a.dt_inicio_real between dt_inicio_p and dt_final_p 
	and	a.cd_medico_cirurgiao 	= cd_medico_p) 
order by 
	nr_atendimento desc;
		
c01_W c01%rowtype;		
		 
nr_atendimentos_w varchar(4000);
		

BEGIN 
 
open c01;
loop 
fetch c01 into c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	nr_atendimentos_w := nr_atendimentos_w || lpad(c01_w.nr_atendimento, 20,' ');	
	end;
end loop;
close c01;
 
nr_atendimentos_w := trim(both nr_atendimentos_w);
 
return	nr_atendimentos_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
