CREATE OR REPLACE FUNCTION obter_atendimentos_pendentes ( cd_pessoa_Fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao 
AC - Atendimentos concatenados 
MX - Maior número de atendimento 
MN - Menos número de atenidmento 
ASC - Atendimentos / (Setores) concatenados */
 
 
ds_retorno_w		varchar(2000);
nr_atendimento_w	bigint;
ds_setor_atendimento_w	varchar(100);

c01 CURSOR FOR 
	SELECT	nr_atendimento, 
		substr(obter_nome_setor(obter_Setor_Atendimento(nr_atendimento)),1,100) 
	from	atendimento_paciente 
	where	cd_pessoa_fisica = cd_pessoa_Fisica_p 
	and	coalesce(dt_alta::text, '') = '';


BEGIN 
 
case	ie_opcao_p 
	/* Atendimentos concatenados */
 
	when 	'AC' then 
		open c01;
		loop 
		fetch 	c01 into 
			nr_atendimento_w, 
			ds_setor_atendimento_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			ds_retorno_w := ds_retorno_w || ', ' || nr_atendimento_w;
		end loop;
		close c01;
		ds_retorno_w := substr(ds_retorno_w, 2, length(ds_retorno_w) - 1);
	/* Maior número de atendimento */
 
	when 	'MX' then 
		select	max(nr_Atendimento) 
		into STRICT	ds_retorno_w 
		from	atendimento_paciente 
		where	cd_pessoa_fisica = cd_pessoa_Fisica_p 
		and	coalesce(dt_alta::text, '') = '';
	/* Menos número de atenidmento */
 
	when 	'MN' then 
		select	min(nr_Atendimento) 
		into STRICT	ds_retorno_w 
		from	atendimento_paciente 
		where	cd_pessoa_fisica = cd_pessoa_Fisica_p 
		and	coalesce(dt_alta::text, '') = '';
	/* Atendimentos / (Setores) concatenados */
 
	when 	'ASC' then 
		open c01;
		loop 
		fetch 	c01 into 
			nr_atendimento_w, 
			ds_setor_atendimento_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			ds_retorno_w := substr(ds_retorno_w || chr(13) || nr_atendimento_w || ' (' || ds_setor_atendimento_w || ')', 1, 2000);
		end loop;
		close c01;
		ds_retorno_w := substr(ds_retorno_w, 2, length(ds_retorno_w) - 1);
end case;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
