CREATE OR REPLACE FUNCTION obter_atendimento_caixa_rec ( nr_seq_caixa_rec_p bigint) RETURNS bigint AS $body$
DECLARE

 
ds_retorno_w bigint;


BEGIN 
 
	SELECT	max(y.nr_atendimento) 
	into STRICT	ds_retorno_w 
	FROM	titulo_receber y, 
		titulo_receber_liq x 
	WHERE	x.nr_titulo = y.nr_titulo 
	AND		x.nr_seq_caixa_rec = nr_seq_caixa_rec_p 
	AND		(y.nr_atendimento IS NOT NULL AND y.nr_atendimento::text <> '');
	 
return	ds_retorno_w;
 
end;
