CREATE OR REPLACE FUNCTION obter_atendimento_dicom (nr_acesso_dicom_p text) RETURNS bigint AS $body$
DECLARE

nr_atendimento_w	bigint;

BEGIN

if (nr_acesso_dicom_p IS NOT NULL AND nr_acesso_dicom_p::text <> '') then
	select	coalesce(max(coalesce(b.nr_atendimento,0)),0) nr_atendimento
	into STRICT	nr_atendimento_w
	from	prescr_procedimento a,
		prescr_medica b
	where	a.nr_prescricao = b.nr_prescricao
	and	a.nr_acesso_dicom = nr_acesso_dicom_p;
end if;

return nr_atendimento_w;

end;
