CREATE OR REPLACE FUNCTION obter_atendimento_rn ( nr_atendimento_p bigint, nr_seq_procedimento_p bigint, nr_seq_material_p bigint, nr_atendimento_mae_p bigint default null, dt_alta_p timestamp default null) RETURNS varchar AS $body$
DECLARE


nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
nr_atendimento_mae_w	atendimento_paciente.nr_atendimento_mae%type;
dt_alta_w		atendimento_paciente.dt_alta%type;
dt_alta_mae_w		atendimento_paciente.dt_alta%type;
qt_reg_w		smallint;
cd_setor_rn_w		procedimento_paciente.CD_SETOR_ATENDIMENTO%type;
cd_setor_mae_w		ATEND_PACIENTE_UNIDADE.CD_SETOR_ATENDIMENTO%type;


BEGIN

nr_atendimento_mae_w	:= nr_atendimento_mae_p;
dt_alta_w		:= dt_alta_p;
nr_atendimento_w	:= nr_atendimento_p;

if (nr_atendimento_mae_w > 0) then

	select	max(a.CD_SETOR_ATENDIMENTO)
	into STRICT	cd_setor_mae_w
	from	ATEND_PACIENTE_UNIDADE a
	where	a.nr_atendimento = nr_atendimento_p;

	select	dt_alta
	into STRICT	dt_alta_mae_w
	from	atendimento_paciente a
	where	a.nr_atendimento	= nr_atendimento_mae_w;

	if (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then

		select	max(a.CD_SETOR_ATENDIMENTO)
		into STRICT	cd_setor_rn_w
		from	procedimento_paciente a
		where	a.NR_SEQUENCIA = nr_seq_procedimento_p;

	elsif (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

		select	max(a.CD_SETOR_ATENDIMENTO)
		into STRICT	cd_setor_rn_w
		from	material_atend_paciente a
		where	a.NR_SEQUENCIA = nr_seq_material_p;

	end if;


	if	((coalesce(dt_alta_mae_w,clock_timestamp()) >= coalesce(dt_alta_w,clock_timestamp()))and (cd_setor_mae_w = cd_setor_rn_w)) then
		nr_atendimento_w	:= nr_atendimento_mae_w;
	end if;

end if;

return	nr_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
