CREATE OR REPLACE FUNCTION obter_atend_agend_consulta (cd_medico_p bigint) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w  bigint := 0;


BEGIN
select *
into STRICT nr_atendimento_w
from (
  SELECT    a.nr_atendimento
  from      agenda_consulta a, agenda_classif b, agenda c
  where     a.IE_CLASSIF_AGENDA = b.CD_CLASSIFICACAO
  and       a.ie_status_agenda = 'O'
  and       b.IE_UTILIZA_TELEMEDICINA = 'S'
  and       a.cd_agenda = c.CD_AGENDA
  and       c.CD_PESSOA_FISICA = cd_medico_p -- param
  and		a.dt_consulta >= clock_timestamp() - interval '1 days'
  order by  a.dt_consulta desc) alias1 LIMIT 1;

return nr_atendimento_w;


END;
$body$
