CREATE OR REPLACE FUNCTION obter_atend_copia_prescr (ie_atend_copiar_p text, nr_atend_paciente_p bigint, cd_estabelecimento_p bigint, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

 
nr_atend_copia_w	bigint;


BEGIN 
if (ie_atend_copiar_p IS NOT NULL AND ie_atend_copiar_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
	if (ie_atend_copiar_p = 'USA') then 
 
		select	coalesce(max(nr_atendimento),nr_atend_paciente_p) 
		into STRICT	nr_atend_copia_w 
		from	atendimento_paciente 
		where	cd_pessoa_fisica = cd_pessoa_fisica_p 
		and	coalesce(dt_alta::text, '') = '';
 
	elsif (ie_atend_copiar_p = 'USAE') then 
 
		select	coalesce(max(nr_atendimento),nr_atend_paciente_p) 
		into STRICT	nr_atend_copia_w 
		from	atendimento_paciente 
		where	cd_estabelecimento = cd_estabelecimento_p 
		and	cd_pessoa_fisica = cd_pessoa_fisica_p 
		and	coalesce(dt_alta::text, '') = '';
 
	else 
	 
		nr_atend_copia_w	:= nr_atend_paciente_p;
 
	end if;
 
else 
 
	nr_atend_copia_w	:= nr_atend_paciente_p;
 
end if;
 
return nr_atend_copia_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
