CREATE OR REPLACE FUNCTION obter_atend_internacao ( nr_atendimento_pa_p bigint) RETURNS varchar AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	nr_atendimento 
	from	atendimento_paciente 
	where	nr_atend_origem_pa = nr_atendimento_pa_p 
	order by nr_atendimento;

nr_atend_pa_w	atendimento_paciente.nr_atendimento%type;
ds_retorno_w	varchar(255);

BEGIN
if (coalesce(nr_atendimento_pa_p,0) > 0) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_atend_pa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		if (coalesce(ds_retorno_w::text, '') = '') then 
			ds_retorno_w := nr_atend_pa_w;
		else 
			ds_retorno_w := substr(ds_retorno_w ||','||nr_atend_pa_w,1,255);
		end if;
		end;
	end loop;
	close C01;
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
