CREATE OR REPLACE FUNCTION obter_atend_laudo_vigente ( cd_pessoa_fisica_p text, dt_referencia_p timestamp, ie_tipo_laudo_p bigint, ie_verifica_conta_p text default 'N', ie_ultimo_atend_pac_p text default 'N') RETURNS bigint AS $body$
DECLARE


nr_atendimento_w	bigint;
ie_libera_w		varchar(1);


BEGIN

select	coalesce(vl_parametro,vl_parametro_padrao)
into STRICT	ie_libera_w
from	funcao_parametro
where	cd_funcao	= 0
and	nr_sequencia	= 83;

if (ie_tipo_laudo_p in (1,4)) then
	select	max(b.nr_atendimento)
	into STRICT	nr_atendimento_w
	from	sus_laudo_paciente b,
		atendimento_paciente a
	where	a.nr_atendimento	= b.nr_atendimento
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((ie_libera_w = 'N') or (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''))
	and	b.ie_tipo_laudo_apac in (1,4)
	and	((trunc(dt_referencia_p) between trunc(b.dt_inicio_val_apac) and trunc(b.dt_fim_val_apac)) or (coalesce(b.dt_inicio_val_apac::text, '') = '') or (coalesce(b.dt_fim_val_apac::text, '') = ''))
	and	((coalesce(ie_verifica_conta_p,'N') = 'N') or (coalesce(a.dt_fim_conta::text, '') = ''));
else
	select	max(b.nr_atendimento)
	into STRICT	nr_atendimento_w
	from	sus_laudo_paciente b,
		atendimento_paciente a
	where	a.nr_atendimento	= b.nr_atendimento
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((ie_libera_w = 'N') or (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''))
	and	b.ie_tipo_laudo_apac	= ie_tipo_laudo_p
	and	((trunc(dt_referencia_p) between trunc(b.dt_inicio_val_apac) and trunc(b.dt_fim_val_apac)) or (coalesce(b.dt_inicio_val_apac::text, '') = '') or (coalesce(b.dt_fim_val_apac::text, '') = ''))
	and	((coalesce(ie_verifica_conta_p,'N') = 'N') or (coalesce(a.dt_fim_conta::text, '') = ''));
end if;

if (coalesce(nr_atendimento_w::text, '') = '') and (coalesce(ie_ultimo_atend_pac_p,'N') = 'S') then
	select	max(nr_Atendimento)
	into STRICT	nr_atendimento_w
	from	atendimento_paciente
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(dt_alta::text, '') = ''
	and	((coalesce(ie_verifica_conta_p,'N') = 'N') or (coalesce(dt_fim_conta::text, '') = ''));
end if;

return	nr_atendimento_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
