CREATE OR REPLACE FUNCTION obter_atend_max_dor (dt_sinal_vital_p timestamp) RETURNS bigint AS $body$
DECLARE



ds_retorno_w		bigint;
nr_seq_result_dor_w	bigint;
qt_intensidade_w	varchar(255);


BEGIN

select 	max(obter_desc_result_dor(nr_seq_result_dor,'I'))
	into STRICT	qt_intensidade_w
	from 	w_eis_escala_dor
	where 	trunc(dt_sinal_vital) = trunc(dt_sinal_vital_p);

select	nr_sequencia
into STRICT	nr_seq_result_dor_w
from   	escala_dor_regra
where  	ie_situacao = 'A'
and    	qt_intensidade = (qt_intensidade_w)::numeric;

select	count(*)
into STRICT	ds_retorno_w
from	w_eis_escala_dor
where	trunc(dt_sinal_vital) = trunc(dt_sinal_vital_p)
and	nr_seq_result_dor  = nr_seq_result_dor_w;

return	ds_retorno_w;

end;
