CREATE OR REPLACE FUNCTION obter_atend_sem_alta_se (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, ie_restringe_estab_p text) RETURNS bigint AS $body$
DECLARE

 
nr_atendimento_w	bigint;
		
			 

BEGIN 
 
select	coalesce(max(nr_atendimento),0) 
into STRICT	nr_atendimento_w 
from	atendimento_paciente 
where	cd_pessoa_fisica = cd_pessoa_fisica_p 
and	coalesce(dt_alta::text, '') = '' 
and	((cd_estabelecimento = cd_estabelecimento_p) or (coalesce(ie_restringe_estab_p,'N') = 'N'));
 
return	nr_atendimento_w;
 
end;
