CREATE OR REPLACE FUNCTION obter_atend_setor_data ( cd_setor_atendimento_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

			 
cont_w		bigint;

BEGIN
select	count(distinct a.cd_pessoa_fisica) 
into STRICT	cont_w 
from	atendimento_paciente a, 
	atend_paciente_unidade b 
where	a.nr_atendimento = b.nr_atendimento 
and	b.cd_setor_atendimento	= cd_setor_atendimento_p 
and	dt_referencia_p between trunc(b.dt_entrada_unidade,'dd') and fim_dia(coalesce(b.dt_saida_unidade,clock_timestamp())) 
and	coalesce(ie_passagem_setor,'N') = 'N';
	 
return cont_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
