CREATE OR REPLACE FUNCTION obter_atend_s_alta_setor_excl ( cd_pessoa_fisica_p text, cd_setor_exclusivo_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_atendimento_w	bigint;
		
			 

BEGIN 
 
if (cd_setor_exclusivo_p IS NOT NULL AND cd_setor_exclusivo_p::text <> '')then 
	begin 
	 
	select	coalesce(max(a.nr_atendimento),0) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente a, 
			atend_paciente_unidade b			 
	where	a.nr_atendimento		= b.nr_atendimento		 
	and		b.cd_setor_atendimento	= cd_setor_exclusivo_p 
	and		a.cd_pessoa_fisica 		= cd_pessoa_fisica_p 
	and		coalesce(a.dt_alta::text, '') = '';
	 
	end;
end if;
 
return	nr_atendimento_w;
 
end;
$body$
