CREATE OR REPLACE FUNCTION obter_atend_valor_conta (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_conta_w		double precision := 0;
vl_total_w		double precision := 0;
nr_interno_conta_w	bigint;		
		 
C01 CURSOR FOR 
SELECT	nr_interno_conta 
from	conta_paciente 
where	nr_atendimento = nr_atendimento_p;
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	coalesce(obter_valor_conta(nr_interno_conta_w, 0),0) 
	into STRICT	vl_conta_w 
	;
	 
	vl_total_w := vl_total_w + vl_conta_w;
	 
	end;
end loop;
close C01;
 
return	vl_total_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
