CREATE OR REPLACE FUNCTION obter_ating_kpi_bsc ( nr_seq_indicador_p bigint, ref_date_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_real_w bsc_ind_inf.qt_meta%type;


BEGIN

	SELECT	max(a.qt_real)
	into STRICT	qt_real_w
	FROM	bsc_ind_inf_v a
	WHERE	a.nr_seq_indicador = nr_seq_indicador_p
	and	a.cd_empresa = '1'
	and	a.ds_periodo = to_char(ref_date_p, 'mm/yyyy');
	
	return qt_real_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
