CREATE OR REPLACE FUNCTION obter_ativ_sup (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_etapa_w		bigint;
nr_seq_superior_w	bigint;


BEGIN
select 	coalesce(c.nr_seq_etapa,0),
	coalesce(c.nr_seq_superior,0)
into STRICT	nr_seq_etapa_w,
	nr_seq_superior_w
from 	proj_cron_etapa c
where 	c.nr_sequencia = nr_sequencia_p;

while(nr_seq_etapa_w = 0) and (nr_seq_superior_w > 0 ) loop
	begin
	select 	coalesce(c.nr_seq_etapa,0),
		coalesce(c.nr_seq_superior,0)
	into STRICT	nr_seq_etapa_w,
		nr_seq_superior_w
	from 	proj_cron_etapa c
	where 	c.nr_sequencia = nr_seq_superior_w;
	end;
end loop;

return	nr_seq_superior_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
