CREATE OR REPLACE FUNCTION obter_atraso_tempo_cirurgico (nr_seq_tempo_p bigint, nr_cirurgia_p bigint, nr_seq_pepo_p bigint) RETURNS bigint AS $body$
DECLARE


qt_tempo_atraso_w	bigint:=null;

BEGIN

if (coalesce(nr_seq_tempo_p,0) > 0) and
	((coalesce(nr_cirurgia_p,0) > 0) or (coalesce(nr_seq_pepo_p,0) > 0)) then
	select	max(qt_tempo_atraso)
	into STRICT	qt_tempo_atraso_w
	from	cirurgia_tempo_atraso
	where	nr_seq_tempo = nr_seq_tempo_p
	and	((nr_cirurgia  = nr_cirurgia_p) and (coalesce(nr_cirurgia_p,0) > 0) or (nr_seq_pepo  = nr_seq_pepo_p) and (coalesce(nr_seq_pepo_p,0) > 0))
	and	coalesce(ie_situacao,'A') = 'A';
end if;

return	qt_tempo_atraso_w;

end	;
$body$
