CREATE OR REPLACE FUNCTION obter_atributo_desc_tabela (nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


nm_atributo_w		varchar(50);


c01 CURSOR FOR
	SELECT nm_atributo
	from tabela_atributo
	where upper(nm_tabela)	= nm_tabela_p
	  and substr(nm_atributo,1,2) in ('NM','DS')
	  and nm_atributo	<> 'NM_USUARIO'
	order by nr_sequencia_criacao desc;


BEGIN

open  c01;

loop
	fetch c01 into nm_atributo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

end loop;

close c01;


return nm_atributo_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
