CREATE OR REPLACE FUNCTION obter_atributo_idioma ( nm_tabela_p text, nm_atributo_p text, nr_seq_visao_p bigint, nr_seq_idioma_p bigint, nm_atrib_trad_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);

BEGIN

if (nr_seq_idioma_p IS NOT NULL AND nr_seq_idioma_p::text <> '') and (nr_seq_idioma_p > 0) then

	if (nr_seq_visao_p IS NOT NULL AND nr_seq_visao_p::text <> '') and (nr_seq_visao_p > 0) then
		select	max(ds_traducao)
		into STRICT	ds_retorno_w
		from	tab_atributo_idioma
		where	nm_tabela = nm_tabela_p
		and	nr_seq_visao = nr_seq_visao_p
		and	nm_atributo = nm_atributo_p
		and	nm_atributo_traducao = nm_atrib_trad_p
		and	nr_seq_idioma = nr_seq_idioma_p;
	else
		select	max(ds_traducao)
		into STRICT	ds_retorno_w
		from	tab_atributo_idioma
		where	nm_tabela = nm_tabela_p
		and	coalesce(nr_seq_visao::text, '') = ''
		and	nm_atributo = nm_atributo_p
		and	nm_atributo_traducao = nm_atrib_trad_p
		and	nr_seq_idioma = nr_seq_idioma_p;
	end if;

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') and (ds_retorno_w <> ' ') then
		return ds_retorno_w;
	end if;

end if;

if (nm_atrib_trad_p = 'DS_LABEL_GRID') then

	if (nr_seq_visao_p IS NOT NULL AND nr_seq_visao_p::text <> '') and (nr_seq_visao_p > 0) then
		select	ds_label_grid
		into STRICT	ds_retorno_w
		from	tabela_visao_atributo
		where	nr_sequencia = nr_seq_visao_p
		and	nm_atributo = nm_atributo_p;
	else
		select	ds_label_grid
		into STRICT	ds_retorno_w
		from	tabela_atributo
		where	nm_tabela = nm_tabela_p
		and	nm_atributo = nm_atributo_p;
	end if;

elsif (nm_atrib_trad_p = 'DS_LABEL') then

	if (nr_seq_visao_p IS NOT NULL AND nr_seq_visao_p::text <> '') and (nr_seq_visao_p > 0) then
		select	ds_label
		into STRICT	ds_retorno_w
		from	tabela_visao_atributo
		where	nr_sequencia = nr_seq_visao_p
		and	nm_atributo = nm_atributo_p;
	else
		select	ds_label
		into STRICT	ds_retorno_w
		from	tabela_atributo
		where	nm_tabela = nm_tabela_p
		and	nm_atributo = nm_atributo_p;
	end if;

end if;

return	ds_retorno_w;

end;
