SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_atributo_tabela (nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


nm_atributo_w		varchar(50);
ds_atributo_w		varchar(4000);
ds_virgula_w		varchar(001);

c01 CURSOR FOR
	SELECT nm_atributo
	from tabela_atributo
	where 	upper(nm_tabela)	= nm_tabela_p
	and	upper(ie_tipo_atributo) not in ('FUNCTION','VISUAL')
	order by nr_sequencia_criacao;


BEGIN
ds_atributo_w		:= '';
ds_virgula_w		:= '';
OPEN  c01;
LOOP
FETCH c01 into nm_atributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_atributo_w	:= ds_atributo_w || ds_virgula_w || nm_atributo_w;
	ds_virgula_w	:= ',';		
END LOOP;
CLOSE c01;

return ds_atributo_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_atributo_tabela (nm_tabela_p text) FROM PUBLIC;

