SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_atrib_grid_ocul_aval_web () RETURNS varchar AS $body$
DECLARE


qt_regra_w	bigint;
nm_atributo_w	varchar(50);
ds_atributos_w	varchar(2000);

c01 CURSOR FOR
SELECT	a.nm_atributo
from 	tabela_visao_atributo a
where 	a.nr_sequencia = 7180
and 	(a.ds_label_grid IS NOT NULL AND a.ds_label_grid::text <> '')
and	not exists (
		SELECT	1
		from	med_avaliacao_atrib_grid x
		where	x.nm_atributo = a.nm_atributo)
order by
	nm_atributo;


BEGIN
select	count(*)
into STRICT	qt_regra_w
from	med_avaliacao_atrib_grid;

if (qt_regra_w > 0) then
	begin
	open c01;
	loop
	fetch c01 into nm_atributo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (coalesce(ds_atributos_w::text, '') = '') then
			begin
			ds_atributos_w := nm_atributo_w;
			end;
		else
			begin
			ds_atributos_w := ds_atributos_w || ',' || nm_atributo_w;
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;
return ds_atributos_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_atrib_grid_ocul_aval_web () FROM PUBLIC;

